    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String first_name = jTextField1.getText();
        String  last_name = jTextField2.getText();
        String  father_name = jTextField3.getText();
        String  mother_name = jTextField4.getText();
        String  PAN_num = jTextField5.getText();
        String  country = jComboBox1.getSelectedItem().toString();
        int  Aadhar_Num = Integer.parseInt(jTextField6.getText());
        int  Mobile_Num = Integer.parseInt(jTextField7.getText());
        String  email = jTextField8.getText();
        String  adderss = jTextField9.getText();
        String  education = jTextField10.getText();
        String Gender;
        Random AC_num = new Random();
        Random CIF_num = new Random();
        int acc = AC_num.nextInt(999);
        int cif = CIF_num.nextInt(999);
        if(jRadioButton1.isSelected())
            Gender = "Male";
        else
            Gender = "Female";
        if(jCheckBox1.isSelected())
        {
            if(first_name.isEmpty()||last_name.isEmpty()||father_name.isEmpty()||mother_name.isEmpty()||PAN_num.isEmpty()
                    ||country.equals("--Select Country--")||jTextField6.getText().isEmpty()||jTextField7.getText().isEmpty()
                    ||email.isEmpty()||adderss.isEmpty()||education.isEmpty())
            {
                JOptionPane.showMessageDialog(null,"Please fill all the fields correctly");
            }
            else{
                 try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/Bank_Management","root","0007");
            Statement stmt = conn.createStatement();
            String query1 = "insert into Customer_Details values ('" + first_name + "', '"+ last_name+"','"+ father_name+"','"+ mother_name+"','"+ PAN_num+"','"+ country +"',"+Aadhar_Num+","+Mobile_Num+",'"+email+"','"+Gender+"','"+adderss+"','"+education+"');";
            System.out.println("Query = " + query1);
            
            stmt.execute(query1);
            String accmes = "Your Account Number is:- " + String.valueOf(acc);
            String cifmes = "Your CIF Number is:- " + String.valueOf(cif);  
            JOptionPane.showMessageDialog(null, "Account Successfully Created");
            JOptionPane.showMessageDialog(null, accmes);
            JOptionPane.showMessageDialog(null, cifmes);
            String query2 = "insert into Account_Details values ("+acc+","+cif+",'"+ PAN_num+"',"+Mobile_Num+",'"+"Not Selected"+"','"+email+"','"+ country +"',0 )";
            stmt.execute(query2);
            dispose();
            new SignUp().show();          
        } catch (Exception e) {    
            JOptionPane.showMessageDialog(null, e);
        }
            }
        }
        else{
            JOptionPane.showMessageDialog(null,"please agree terms & conditions");
        }
       
    }//GEN-LAST:event_jButton1ActionPerformed

