    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int  AC_num = Integer.parseInt(jTextField1.getText());
        int  CIF_num = Integer.parseInt(jTextField2.getText());
        String  country = jComboBox2.getSelectedItem().toString();
        int  mobile_num = Integer.parseInt(jTextField4.getText());
        String  email = jTextField5.getText();
        String TransRight = jComboBox1.getSelectedItem().toString();
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/Bank_Management","root","0007");
            Statement stmt = conn.createStatement();
            String query = "select * from Account_Details where AccountNumber="+AC_num+" and CIF_Number="+CIF_num+";";
            ResultSet rs = stmt.executeQuery(query);
            rs.next();
                if(AC_num == rs.getInt("AccountNumber") && CIF_num == rs.getInt("CIF_Number") && country.equals(rs.getString("Country")) &&
                        mobile_num == rs.getInt("Mobile_Num") && email.equals(rs.getString("Email_ID")))
                {
                    JOptionPane.showMessageDialog(null, "Sign Up Successfull");
                    String query2 = "update Account_Details set TranscationRigits = '"+TransRight+"'where AccountNumber="+AC_num+";";
                    stmt.execute(query2);
                    dispose();
                    new Username_Password(AC_num);
                    new Username_Password().show();
                }
                else{
                    JOptionPane.showMessageDialog(null, "Please Enter Correct Details");
                }         
        } catch (Exception e) {    
            JOptionPane.showMessageDialog(null, "ERROR : Database Not Connected, Try Again Later");
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

